import React, { useState, useEffect} from 'react';
import './App.css';
import InfoBox from './InfoBox';
import Map from './Map';
import Table from './Table';
import { sortData } from './util';

import {
  MenuItem,
  FormControl,
  Select,
  Card,
  CardContent
} from "@material-ui/core"

function App() {
  const [countries, setCountries] = useState([]);
  const [country, setCountry] = useState('worldwide');
  const [countryInfo, setCountryInfo] = useState({});
  const [tableData, setTableData] = useState([]);
  // STATE = How to write a variable in REACT

  // https://disease.sh/v3/covid-19/countries

  // USEEFFECT = Runs a piece of code
  // based on a given condition


  useEffect(() =>{
	fetch("https://disease.sh/v3/covid-19/all")
	.then(response => response.json())
	.then(data => {
		setCountryInfo(data);
	})
  },[]);

  // onload get countries data from API
  useEffect(() => {
      // async -> send a request , wait for it, to do something with it

	  // get countries from API
      const getCountriesData = async () => {
        await fetch("https://disease.sh/v3/covid-19/countries")
        .then((response) => response.json())
        .then((data) => {
          const countries = data.map((country) => ({
              name: country.country, //United states
              value: country.countryInfo.iso2  // UK, USA, FR
            }));

			const sortedData = sortData(data);
			setTableData(sortedData)
            setCountries(countries)
        });
      };

      getCountriesData();
  }, []);

  //Function for changing Select menu
  const onCountryChange = async (event) => {
    const countryCode = event.target.value;
	setCountry(countryCode)

	// https://disease.sh/v3/covid-19/countries/[COUNTRY_CODE]
	const url = countryCode === 'worldwide' ? 'https://disease.sh/v3/covid-19/all' : `https://disease.sh/v3/covid-19/countries/${countryCode}`;

	await fetch(url)
	.then(response => response.json())
	.then(data=> {
		setCountry(countryCode);

		// All of the data from the country response
		setCountryInfo(data);
	});
  };

  return (
    <div className="app">
		<div className="app__left">
			<div className="app__header">
				<h1>COVID-19 TRACKER</h1>
				<FormControl className="app__dropdown">
					<Select variant="outlined" onChange={onCountryChange} value={country}>
						<MenuItem value="worldwide">Worldwide</MenuItem>   
						{
						countries.map((country) =>(
							<MenuItem value={country.value}>{country.name}</MenuItem>    
						))
						}
					</Select> 
				</FormControl>
			</div>

			<div className="app__stats">
				<InfoBox title="Coronavirus Cases" cases={countryInfo.todayCases} total={countryInfo.cases} />

				<InfoBox title="Recovered" cases={countryInfo.todayRecovered} total={countryInfo.todayRecovered} />

				<InfoBox title="Deaths" cases={countryInfo.todayDeaths} total={countryInfo.todayDeaths} />
			</div> 
			<Map />
		</div>
		<div className="app__right">
			<Card>
				<CardContent>
					<h3>Live Cases by Country</h3>
					<Table countries={tableData} />
				</CardContent>
			</Card>
		</div>
    </div>
  );
}

export default App;
